# === DÉFINITION DES MÉTHODES ===

def count_journalists(list)
  puts "\n1. Il y a #{list.size} journalistes dans la base."
end

def count_with_numbers(list)
  puts "\n2. #{list.count { |h| h =~ /\d/ }} handles contiennent un numéro."
end

def count_with_aude(list)
  puts "\n3. #{list.count { |h| h.downcase.include?('aude') }} handles contiennent 'aude'."
end

def count_capital_first(list)
  puts "\n4. #{list.count { |h| h[1] =~ /[A-Z]/ }} handles commencent par une majuscule."
end

def count_with_uppercase(list)
  puts "\n5. #{list.count { |h| h =~ /[A-Z]/ }} handles contiennent au moins une majuscule."
end

def count_underscores(list)
  puts "\n6. On a trouvé #{list.join.count('_')} underscores '_' dans toute la base."
end

def sorted_handles(list)
  puts "\n7. Les 5 premiers handles triés alphabétiquement :"
  puts list.sort.first(5)
end

def shortest_50_handles(list)
  puts "\n8. Les 50 handles les plus courts :"
  puts list.sort_by(&:length).first(50)
end

def find_epenser(list)
  pos = list.index('@epenser')
  if pos
    puts "\n9. Le handle @epenser se trouve à la position #{pos} dans l'array."
  else
    puts "\n10. @epenser n’est pas dans la liste !"
  end
end

# === PROGRAMME PRINCIPAL ===

def perform(list)
  puts "=== ANALYSE BIG DATA DES JOURNALISTES ==="

  puts "\nLes analyses de la base des journalistes révèlent : "

  count_journalists(list)
  count_with_numbers(list)
  count_with_aude(list)
  count_capital_first(list)
  count_with_uppercase(list)
  count_underscores(list)
  sorted_handles(list)
  shortest_50_handles(list)
  find_epenser(list)

  puts "=== FIN DU RAPPORT BIG DATA ==="
end

# LANCEMENT DU PROGRAMME
perform(journalists)